package p0924;

public class for3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		System.out.println("---for---");		for(i=0; i<=4; i++){
			System.out.println("i의 값은 : "+i);
		}
		System.out.println("---while---");
		i=0;
		while(i<=4){
			System.out.println("i의 값은 : "+i);
			i++;
		}
	}

}
